CREATE OR REPLACE PROCEDURE pls_liberar_lote_giss (nr_seq_lote_p bigint, nm_usuario_p text, ie_liberar_p text) AS $body$
BEGIN

if (coalesce(nr_seq_lote_p,0) <> 0) then

	if (ie_liberar_p = 'S') then
		update	pls_lote_envio_giss
		set	dt_liberacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_lote_p;
	end if;

	if (ie_liberar_p = 'N') then
		update	pls_lote_envio_giss
		set	dt_liberacao	 = NULL
		where	nr_sequencia	= nr_seq_lote_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
