CREATE OR REPLACE PROCEDURE pls_liberar_lote_mens ( nr_seq_mens_lib_p pls_mensalidade_liberacao.nr_sequencia%type, cd_perfil_p perfil.cd_perfil%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_mens_lib_w	bigint;
					

BEGIN

select	count(1)
into STRICT	qt_mens_lib_w
from	pls_mensalidade_lib_perfil
where	nr_seq_liberacao = nr_seq_mens_lib_p
and	((nm_usuario_liberacao = nm_usuario_p) or (cd_perfil_liberacao = cd_perfil_p));

if (qt_mens_lib_w > 0) then
	update	pls_mensalidade_liberacao
	set	nm_usuario 		= nm_usuario_p,
		nm_usuario_liberacao 	= nm_usuario_p,
		dt_liberacao 		= clock_timestamp(),
		dt_atualizacao 		= clock_timestamp()
	where	nr_sequencia 		= nr_seq_mens_lib_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1133779);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
