CREATE OR REPLACE PROCEDURE pls_liberar_lote_tx_atend ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Liberar o lote de pós-estabelecido
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
	OPS - Controle de Coparticipações
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_tx_atend_w		pls_conta_val_atend.nr_sequencia%type;
dt_competencia_mens_w		pls_lote_taxa_atend.dt_competencia_mens%type;

C01 CURSOR FOR
	SELECT	a.nr_seq_tx_atend
	from	pls_lib_taxa_atend a,
		pls_conta_val_atend b
	where	a.nr_seq_tx_atend = b.nr_sequencia
	and	a.nr_seq_lote	= nr_seq_lote_p
	and	coalesce(b.ie_cobrar_mensalidade,'P') = 'P';


BEGIN

select	max(dt_competencia_mens)
into STRICT	dt_competencia_mens_w
from	pls_lote_taxa_atend
where	nr_sequencia	= nr_seq_lote_p;

open C01;
loop
fetch C01 into
	nr_seq_tx_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_conta_val_atend
	set	ie_cobrar_mensalidade	= 'S',
		dt_competencia_mens	= dt_competencia_mens_w
	where	nr_sequencia	= nr_seq_tx_atend_w;
	end;
end loop;
close C01;

update	pls_lote_taxa_atend
set	dt_liberacao		= clock_timestamp(),
	nm_usuario_liberacao	= nm_usuario_p,
	ie_status		= 'D'
where	nr_sequencia		= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
