CREATE OR REPLACE PROCEDURE pls_liberar_nova_geracao ( nr_seq_lote_p pls_cta_lote_processo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

ie_libera_w	varchar(1);
nm_id_sid_w	pls_cta_lote_processo.nm_id_sid%type;
nm_id_serial_w	pls_cta_lote_processo.nm_id_serial%type;

BEGIN

ie_libera_w := pls_pode_liberar_nova_geracao(	nr_seq_lote_p);

if (ie_libera_w = 'S') then

	-- se pode liberar atualiza o status
	update	pls_cta_lote_processo
	set	ie_status = '1',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;
	commit;
else
	-- busca os dados para exibir na mensagem
	select	max(nm_id_sid),
		max(nm_id_serial)
	into STRICT	nm_id_sid_w,
		nm_id_serial_w
	from	pls_cta_lote_processo
	where	nr_sequencia = nr_seq_lote_p;

	CALL wheb_mensagem_pck.exibir_mensagem_abort(271749,'NR_SEQ_LOTE='||nr_seq_lote_p||';NM_ID_SID='||nm_id_sid_w||';NM_ID_SERIAL='||nm_id_serial_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
