SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_ocorren_combinada ( nr_seq_ocorrencia_p bigint, nr_seq_ocor_combinada_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Liberar a ocorrência combinada
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Quando liberar uma ocorrência deve ser feita a inativação de todas as outras.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registros_w				bigint;

BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_ocor_aut_combinada
where	nr_seq_ocorrencia	= nr_seq_ocorrencia_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	nr_sequencia		<> nr_seq_ocor_combinada_p;

if (qt_registros_w	> 0) then
	update	pls_ocor_aut_combinada
	set	dt_inativacao_regra	= clock_timestamp(),
		dt_liberacao		 = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_seq_ocorrencia	= nr_seq_ocorrencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	nr_sequencia		<> nr_seq_ocor_combinada_p;
end if;

update	pls_ocor_aut_combinada
set	dt_liberacao		= clock_timestamp(),
	dt_inativacao_regra	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_seq_ocorrencia	= nr_seq_ocorrencia_p
and	nr_sequencia		= nr_seq_ocor_combinada_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
