CREATE OR REPLACE PROCEDURE pls_liberar_preco_segurado ( nr_seq_seg_preco_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/*
ie_opcao_p
	L = Liberar
	D = Desfazer liberação
*/
BEGIN
if (ie_opcao_p = 'L') then
	update	pls_segurado_preco
	set	dt_liberacao		= clock_timestamp(),
		nm_usuario_liberacao	= nm_usuario_p
	where	nr_sequencia 		= nr_seq_seg_preco_p;
elsif (ie_opcao_p = 'D') then
	update	pls_segurado_preco
	set	dt_liberacao		 = NULL,
		nm_usuario_liberacao	 = NULL
	where	nr_sequencia 		= nr_seq_seg_preco_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
