CREATE OR REPLACE PROCEDURE pls_liberar_reajuste_sca ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_liberacao_w		timestamp;

C01 CURSOR FOR
	SELECT	dt_liberacao
	from	pls_segurado_preco_origem
	where	nr_seq_lote_reajuste	= nr_seq_lote_p;

BEGIN

open C01;
loop
fetch C01 into
	dt_liberacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(dt_liberacao_w::text, '') = '') then
		update	pls_segurado_preco_origem
		set	dt_liberacao		= clock_timestamp(),
			nm_usuario_liberacao	= nm_usuario_p
		where	nr_seq_lote_reajuste	= nr_seq_lote_p;
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
