SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_regra_apropriacao ( nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_regra_w			pls_regra_apropriacao.nr_sequencia%type;
nr_seq_tabela_w			pls_tabela_preco.nr_sequencia%type;
nr_seq_plano_w			pls_plano.nr_sequencia%type;
nr_seq_classif_dependencia_w	pls_classif_dependencia.nr_sequencia%type;
nr_seq_contrato_w		pls_contrato.nr_sequencia%type;
dt_aprovacao_contrato_w		pls_contrato.dt_aprovacao%type;
dt_inicio_vigencia_w		pls_regra_apropriacao.dt_inicio_vigencia%type;
vl_soma_w			pls_regra_apropriacao_item.vl_apropriacao%type;
tx_soma_aprop_w			pls_regra_apropriacao_item.tx_apropriacao%type;
dt_contrato_w			pls_contrato.dt_contrato%type;
nr_contrato_w			pls_contrato.nr_contrato%type;
qt_registros_w			bigint;
qt_valor_w			bigint;
qt_taxa_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		vl_preco_atual,
		qt_idade_inicial,
		qt_idade_final
	from	pls_plano_preco
	where	nr_seq_tabela	= nr_seq_tabela_w;

c01_w	C01%RowType;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_contrato = nr_seq_contrato_w
	and	nr_seq_plano	= nr_seq_plano_w
	and	nr_seq_tabela	= nr_seq_tabela_w
	and	((nr_seq_classif_dependencia = nr_seq_classif_dependencia_w) or (coalesce(nr_seq_classif_dependencia_w::text, '') = ''))
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

c02_w	C02%RowType;


BEGIN
select	nr_seq_contrato,
	nr_seq_plano,
	nr_seq_tabela,
	nr_seq_classif_dependencia,
	dt_inicio_vigencia
into STRICT	nr_seq_contrato_w,
	nr_seq_plano_w,
	nr_seq_tabela_w,
	nr_seq_classif_dependencia_w,
	dt_inicio_vigencia_w
from	pls_regra_apropriacao
where	nr_sequencia	= nr_sequencia_p;

select	nr_contrato,
	dt_aprovacao,
	dt_contrato
into STRICT	nr_contrato_w,
	dt_aprovacao_contrato_w,
	dt_contrato_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_w;

if (dt_inicio_vigencia_w < trunc(dt_contrato_w,'dd')) then
	--A data de início vigência não pode ser menor que a data do contrato.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296420);
end if;

if (coalesce(nr_seq_plano_w::text, '') = '') then
	--Favor informar o produto para a regra.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296457);
end if;

if (coalesce(nr_seq_tabela_w::text, '') = '') then
	--Favor informar a tabela de preço para a regra.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296458);
end if;

select	count(1)
into STRICT	qt_registros_w
from	pls_contrato_plano
where	nr_seq_contrato	= nr_seq_contrato_w
and	nr_seq_plano	= nr_seq_plano_w;

if (qt_registros_w = 0) then
	-- O produto #@NR_SEQ_PLANO#@ não está vinculado ao contrato #@NR_CONTRATO#@. Favor verifique.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(296454,'NR_SEQ_PLANO='||nr_seq_plano_w||';NR_CONTRATO='||nr_contrato_w);
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_regra_apropriacao
where	nr_sequencia			<> nr_sequencia_p
and	nr_seq_contrato			= nr_seq_contrato_w
and	nr_seq_plano			= nr_seq_plano_w
and	nr_seq_tabela			= nr_seq_tabela_w
and	nr_seq_classif_dependencia	= nr_seq_classif_dependencia_w
and	coalesce(dt_fim_vigencia::text, '') = '';

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	/*Não é possível liberar a regra.
	Já existe a regra #@NR_SEQ_REGRA#@ ativa para a mesma tabela de preço, produto, contrato e classificação de dependência. Favor verificar!*/
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(295195,'NR_SEQ_REGRA=' || to_char(nr_seq_regra_w));
end if;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_valor_w
	from	pls_regra_apropriacao_item
	where	nr_seq_apropriacao	= nr_sequencia_p
	and	nr_seq_preco		= c01_w.nr_sequencia
	and	(vl_apropriacao IS NOT NULL AND vl_apropriacao::text <> '');

	select	count(1)
	into STRICT	qt_taxa_w
	from	pls_regra_apropriacao_item
	where	nr_seq_apropriacao	= nr_sequencia_p
	and	nr_seq_preco		= c01_w.nr_sequencia
	and	(tx_apropriacao IS NOT NULL AND tx_apropriacao::text <> '');

	if (qt_valor_w = 0) and (qt_taxa_w = 0) then
		--Favor informar os valores para faixa etária de #@QT_IDADE_INICIAL#@ à #@QT_IDADE_FINAL#@ anos.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(296406,'QT_IDADE_INICIAL=' || to_char(c01_w.qt_idade_inicial) || ';QT_IDADE_FINAL=' || to_char(c01_w.qt_idade_final));
	elsif (qt_valor_w <> 0) and (qt_taxa_w <> 0) then
		--Favor informar apenas a taxa ou o valor para faixa etária de #@QT_IDADE_INICIAL#@ à #@QT_IDADE_FINAL#@ anos.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(296407,'QT_IDADE_INICIAL=' || to_char(c01_w.qt_idade_inicial) || ';QT_IDADE_FINAL=' || to_char(c01_w.qt_idade_final));
	elsif (qt_valor_w > 0) then
		select	sum(vl_apropriacao)
		into STRICT	vl_soma_w
		from	pls_regra_apropriacao_item
		where	nr_seq_apropriacao	= nr_sequencia_p
		and	nr_seq_preco		= c01_w.nr_sequencia;

		if (vl_soma_w <> c01_w.vl_preco_atual) then
			--O valor da apropriação não confere com o valor da faixa etária de #@QT_IDADE_INICIAL#@ à #@QT_IDADE_FINAL#@ anos.
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(296408,'QT_IDADE_INICIAL=' || to_char(c01_w.qt_idade_inicial) || ';QT_IDADE_FINAL=' || to_char(c01_w.qt_idade_final));
		end if;

		select	count(1)
		into STRICT	qt_registros_w
		from	pls_regra_apropriacao_item
		where	nr_seq_apropriacao	= nr_sequencia_p
		and	nr_seq_preco		= c01_w.nr_sequencia
		and	ie_permite_reajustar = 'S';

		if (qt_registros_w = 0) then
			--A opção Permite reajustar apropriação deve estar marcada em pelo menos uma apropriação da faixa etária de #@QT_IDADE_INICIAL#@ à #@QT_IDADE_FINAL#@ anos.
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(295205,'QT_IDADE_INICIAL=' || to_char(c01_w.qt_idade_inicial) || ';QT_IDADE_FINAL=' || to_char(c01_w.qt_idade_final));
		end if;
	elsif (qt_taxa_w > 0) then
		select	sum(tx_apropriacao)
		into STRICT	tx_soma_aprop_w
		from	pls_regra_apropriacao_item
		where	nr_seq_apropriacao	= nr_sequencia_p
		and	nr_seq_preco		= c01_w.nr_sequencia;

		if (tx_soma_aprop_w <> 100) then
			--A taxa da apropriação deve ser 100%. Verificar faixa etária de #@QT_IDADE_INICIAL#@ à #@QT_IDADE_FINAL#@ anos.
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(296410,'QT_IDADE_INICIAL=' || to_char(c01_w.qt_idade_inicial) || ';QT_IDADE_FINAL=' || to_char(c01_w.qt_idade_final));
		end if;
	end if;
	end;
end loop;
close C01;

update	pls_regra_apropriacao
set	dt_liberacao		= clock_timestamp(),
	nm_usuario_liberacao	= nm_usuario_p
where	nr_sequencia		= nr_sequencia_p;

if (dt_aprovacao_contrato_w IS NOT NULL AND dt_aprovacao_contrato_w::text <> '') then
	open C02;
	loop
	fetch C02 into
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		CALL pls_preco_beneficiario_pck.gravar_preco_benef(c02_w.nr_sequencia, 'AR', 'S', dt_inicio_vigencia_w, 'N', null, nm_usuario_p, cd_estabelecimento_p);
		end;
	end loop;
	close C02;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_liberar_regra_apropriacao ( nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

