SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_reg_auxiliar (nr_seq_registro_p bigint, ie_liberar_p text) AS $body$
DECLARE


nm_usuario_w		usuario.nm_usuario%type;


BEGIN

nm_usuario_w	:= wheb_usuario_pck.get_nm_usuario;


if (coalesce(nr_seq_registro_p,0) <> 0) then

	if (ie_liberar_p = 'S') then
		update	ctb_livro_auxiliar
		set	dt_liberacao	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_w
		where	nr_sequencia	= nr_seq_registro_p;
	end if;

	if (ie_liberar_p = 'N') then
		update	ctb_livro_auxiliar
		set	dt_liberacao	 = NULL,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_w
		where	nr_sequencia	= nr_seq_registro_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
