CREATE OR REPLACE PROCEDURE pls_liberar_relat_mala_direta ( nr_seq_tipo_mala_p bigint, cd_perfil_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE

 
/* Procedure utilizada para selecionar os relatorios a serem usados na função OPS - Mala direta para correspondência */
 
nr_seq_relatorio_w	bigint;
ds_lista_w				varchar(255);
cd_relatorio_w			integer;

C01 CURSOR FOR 
	SELECT	a.nr_seq_relatorio 
	from	pls_regra_mala_direta a, 
			relatorio_perfil b 
	where	a.nr_seq_relatorio	= b.nr_seq_relatorio 
	and	b.cd_perfil	= cd_perfil_p 
	and	a.nr_seq_tipo_mala	= nr_seq_tipo_mala_p;
	

BEGIN 
 
ds_lista_w	:= '';
 
if (coalesce(nr_seq_tipo_mala_p,0) <> 0) then 
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_relatorio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		select	cd_relatorio 
		into STRICT		cd_relatorio_w 
		from		relatorio 
		where		nr_sequencia = nr_seq_relatorio_w;
		 
		ds_lista_w := ds_lista_w || to_char(cd_relatorio_w) || ',';
	end loop;
	close C01;
end if;
 
ds_retorno_p	:= ds_lista_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
