CREATE OR REPLACE PROCEDURE pls_liberar_repasses_segurado ( nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_seg_repasse_w	bigint;
			
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_segurado_repasse 
	where	nr_seq_segurado	= nr_seq_segurado_p 
	and	coalesce(dt_liberacao::text, '') = '';

/*Essa procedure irá liberar todos os repasses para o beneficiário*/
 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_seg_repasse_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	CALL pls_liberar_repasse_seg(nr_seq_seg_repasse_w,nm_usuario_p,'S');
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
