SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_repasse ( nr_seq_repasse_mens_p bigint, dt_liberacao_p timestamp, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_P bigint) AS $body$
DECLARE


/*
ie_opcao_p:
	"L" = Liberar
	"D" = Desfazer liberação
*/
qt_tit_liquidados_w		integer;
ie_liberar_repasse_w		varchar(1);
ie_lote_gerado_w		varchar(1);
qt_dias_limite_liber_w		bigint;
dt_limite_liberacao_w		timestamp;
dt_liberacao_w			pls_repasse_mens.dt_liberacao%type;
dt_cancelamento_w		pls_repasse_mens.dt_cancelamento%type;


BEGIN

ie_liberar_repasse_w 	:= coalesce(obter_valor_param_usuario(1206, 13, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_P), 'N');
qt_dias_limite_liber_w 	:= coalesce(obter_valor_param_usuario(1206, 15, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_P), 0);

if (ie_opcao_p = 'L') then
	select	dt_liberacao,
		dt_cancelamento
	into STRICT	dt_liberacao_w,
		dt_cancelamento_w
	from	pls_repasse_mens
	where	nr_sequencia	= nr_seq_repasse_mens_p;

	if (coalesce(dt_liberacao_w::text, '') = '') and (coalesce(dt_cancelamento_w::text, '') = '') then
		select	count(1)
		into STRICT	qt_tit_liquidados_w
		from	titulo_receber			d,
			pls_mensalidade 		a,
			pls_mensalidade_segurado 	b,
			pls_repasse_mens 		c
		where	a.nr_sequencia		= b.nr_seq_mensalidade
		and	c.nr_seq_mens_seg	= b.nr_sequencia
		and	a.nr_sequencia		= d.nr_seq_mensalidade
		and	c.nr_sequencia		= nr_seq_repasse_mens_p
		and	(d.dt_liquidacao IS NOT NULL AND d.dt_liquidacao::text <> '')
		and	d.ie_situacao not in ('3','5','6')
		and	coalesce(a.ie_cancelamento,'N') not in ('C','E');

		if (qt_tit_liquidados_w = 0) and (ie_liberar_repasse_w = 'N') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort( 267331, null ); /* A liberação do item não pode ser realizada pois o título da mensalidade não está liquidado. */
		end if;

		if (qt_dias_limite_liber_w > 0) then
			select (a.dt_contratacao + qt_dias_limite_liber_w)
			into STRICT	dt_limite_liberacao_w
			from	pls_repasse_mens 	 b,
				pls_segurado		 a
			where	a.nr_sequencia		= b.nr_seq_segurado
			and	b.nr_sequencia		= nr_seq_repasse_mens_p;

			if (dt_limite_liberacao_w < dt_liberacao_p) then
				CALL wheb_mensagem_pck.exibir_mensagem_abort( 267333, null ); /* A data limite para liberar este(s) beneficiário(s) foi alcançada! Favor verificar. (Parâmetro 15) */
			end if;
		end if;

		update	pls_repasse_mens
		set	dt_liberacao 	= coalesce(dt_liberacao_p,clock_timestamp()),
			nm_usuario_lib	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_repasse_mens_p;
	end if;
elsif (ie_opcao_p = 'D') then
	update	pls_repasse_mens
	set	dt_liberacao 	 = NULL,
		nm_usuario_lib	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_repasse_mens_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_liberar_repasse ( nr_seq_repasse_mens_p bigint, dt_liberacao_p timestamp, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_P bigint) FROM PUBLIC;

