CREATE OR REPLACE PROCEDURE pls_liberar_req_contratante ( ie_status_p bigint, nr_seq_requisicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_estagio_w			smallint;


BEGIN

if (ie_status_p = 0) then
	ie_estagio_w := 9;
elsif (ie_status_p = 1) then
	ie_estagio_w := 7;
end if;

update	pls_requisicao
set	ie_estagio	= ie_estagio_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_requisicao_p;

commit;

end;
