SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_sub_estipulante ( nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
ie_permite_tab_dif_w		varchar(1);
ie_consiste_tab_contr_w		varchar(1);
ie_gerar_cartao_w		varchar(1);
nr_seq_seg_contrato_w		bigint;
nr_seq_contrato_w		bigint;
qt_existente_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_segurado a,
		pls_contrato b
	where	b.nr_sequencia = a.nr_seq_contrato
	and	a.nr_seq_subestipulante	= nr_sequencia_p
	and	(b.dt_aprovacao IS NOT NULL AND b.dt_aprovacao::text <> '')
	and	coalesce(a.dt_liberacao::text, '') = ''
	order by
		a.nr_seq_titular desc;


BEGIN

/* Leitura do parametro 3 da OPS - Gestao de contratos */

begin
select	coalesce(max(obter_valor_param_usuario(1202, 3, Obter_Perfil_Ativo, nm_usuario_p, 0)), 'N')
into STRICT	ie_gerar_cartao_w
;
exception
when others then
	ie_gerar_cartao_w	:= 'N';
end;

/* Leitura do parametro 9 da OPS - Gestao de contratos */

begin
select	coalesce(max(obter_valor_param_usuario(1202, 9, Obter_Perfil_Ativo, nm_usuario_p, 0)), 'N')
into STRICT	ie_permite_tab_dif_w
;
exception
when others then
	ie_permite_tab_dif_w	:= 'N';
end;

/* Leitura do parametro 10 da OPS - Gestao de contratos */

begin
select	coalesce(max(obter_valor_param_usuario(1202, 10, Obter_Perfil_Ativo, nm_usuario_p, 0)), 'N')
into STRICT	ie_consiste_tab_contr_w
;
exception
when others then
	ie_consiste_tab_contr_w	:= 'N';
end;

update	pls_sub_estipulante
set	dt_liberacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;

open C01;
loop
fetch C01 into	
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_seq_seg_contrato),
		max(nr_seq_contrato)
	into STRICT	nr_seq_seg_contrato_w,
		nr_seq_contrato_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_w;
	
	select	count(*)
	into STRICT	qt_existente_w
	from	pls_segurado
	where	nr_seq_seg_contrato	= nr_seq_seg_contrato_w
	and	nr_seq_contrato		= nr_seq_contrato_w;
	
	if (qt_existente_w > 1) then
		select	max(nr_seq_seg_contrato)+1
		into STRICT	nr_seq_seg_contrato_w
		from	pls_segurado
		where	nr_seq_contrato		= nr_seq_contrato_w;
		
		update	pls_segurado
		set	nr_seq_seg_contrato = nr_seq_seg_contrato_w
		where	nr_sequencia = nr_seq_segurado_w;
		
	end if;
	
	CALL pls_gerar_valor_segurado(null, nr_seq_segurado_w, 'S',
		cd_estabelecimento_p, nm_usuario_p, 'N',
		null, ie_permite_tab_dif_w, ie_consiste_tab_contr_w,
		ie_gerar_cartao_w, 'N');
		
	CALL pls_desbloquear_auto_cartao(nr_seq_segurado_w, 'M', nm_usuario_p, cd_estabelecimento_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
