CREATE OR REPLACE PROCEDURE pls_liberar_tabela_proposta ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_proposta_adesao_w		varchar(1) := 'S';


BEGIN

select	coalesce(ie_proposta_adesao,'N')
into STRICT	ie_proposta_adesao_w
from	pls_tabela_preco
where	nr_sequencia	= nr_sequencia_p;

if (ie_proposta_adesao_w	= 'S') then
	ie_proposta_adesao_w	:= 'N';
elsif (ie_proposta_adesao_w	= 'N') then
	ie_proposta_adesao_w	:= 'S';
end if;

update	pls_tabela_preco
set	ie_proposta_adesao    	= ie_proposta_adesao_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia 		= nr_sequencia_p;

commit;

end;
$body$
