CREATE OR REPLACE PROCEDURE pls_liberar_termo_uso ( nr_seq_termo_p pls_termo_responsabilidade.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_tipo_acao_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN
/*
* L - Liberar
* I - Inativar
*/
if (ie_tipo_acao_p = 'L') then
	update	pls_termo_responsabilidade
	set		dt_liberacao 	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
	where	nr_sequencia 	= nr_seq_termo_p;
elsif (ie_tipo_acao_p = 'I') then
	update	pls_termo_responsabilidade
	set		ie_situacao 	= 'I',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
	where	nr_sequencia 	= nr_seq_termo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
