SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_todos_grupos ( nr_seq_auditoria_p bigint, nm_usuario_p text, ie_existe_item_pendente_p INOUT text) AS $body$
DECLARE


nr_seq_grupo_auditoria_w	bigint;
nr_seq_grupo_w			bigint;
ie_pertence_grupo_w		varchar(1);
ie_existe_item_pendente_w	varchar(1) := 'N';
qt_grupo_w			integer;
qt_item_pendente_w		integer;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_grupo
	from	pls_auditoria_grupo
	where	nr_seq_auditoria	= nr_seq_auditoria_p
	and	coalesce(dt_liberacao::text, '') = ''
	order	by nr_seq_ordem;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_grupo_auditoria_w,
	nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_pertence_grupo_w :=	pls_obter_se_auditor_grupo(nr_seq_grupo_w,nm_usuario_p);

	if (ie_pertence_grupo_w = 'S') then
		select	count(*)
		into STRICT	qt_grupo_w
		from	pls_auditoria_grupo
		where	nr_seq_auditoria	= nr_seq_auditoria_p
		and	coalesce(dt_liberacao::text, '') = '';

		select	count(*)
		into STRICT	qt_item_pendente_w
		from	pls_auditoria_item
		where	nr_seq_auditoria	= nr_seq_auditoria_p
		and	ie_status = 'P';

		if (qt_grupo_w = 1) and (qt_item_pendente_w > 0) then
			ie_existe_item_pendente_w := 'S';
		else
			update	pls_auditoria_grupo
			set	dt_inicio_auditoria = clock_timestamp(),
				dt_liberacao	= clock_timestamp(),
				ie_status	= 'P',
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp(),
				nm_usuario_exec	= nm_usuario_p
			where	nr_sequencia	= nr_seq_grupo_auditoria_w;
		end if;
	else
		goto final;
		nr_seq_grupo_w := 0;
	end if;

	end;
end loop;
close C01;

<<final>>

ie_existe_item_pendente_p := ie_existe_item_pendente_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_liberar_todos_grupos ( nr_seq_auditoria_p bigint, nm_usuario_p text, ie_existe_item_pendente_p INOUT text) FROM PUBLIC;

