CREATE OR REPLACE PROCEDURE pls_liberar_usuario_pericia ( nr_seq_usuario_p bigint, ie_gerar_carteirinha_p text, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_pericia_w		bigint;
qt_dias_validade_w		integer	:= 90;
dt_validade_w			timestamp;


BEGIN 
 
/* Leitura do par√¢metro 2 */
 
begin 
select	coalesce(max(obter_valor_param_usuario(1235, 2, Obter_Perfil_Ativo, nm_usuario_p, 0)), 90) 
into STRICT	qt_dias_validade_w
;
exception 
when others then 
	qt_dias_validade_w	:= 90;
end;
dt_validade_w	:= clock_timestamp() + qt_dias_validade_w;
 
if (ie_gerar_carteirinha_p	= 'S') then 
	CALL pls_gerar_carteira_usuario(nr_seq_usuario_p, null, clock_timestamp(), 
				dt_validade_w, 'P', 'N',null, nm_usuario_p);
end if;
 
CALL pls_liberar_segurado(nr_seq_usuario_p, '', nm_usuario_p, 'S');
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
