SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_lib_declaracao_web ( nr_seq_declaracao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			varchar(2);
qt_registro_w			bigint;


BEGIN

select	ie_status
into STRICT	ie_status_w
from	pls_declaracao_segurado
where	nr_sequencia	= nr_seq_declaracao_p;

if (ie_opcao_p = 'L') then
	select 	count(*)
	into STRICT 	qt_registro_w
	from 	pls_declaracao_resposta
	where 	nr_seq_declaracao = nr_seq_declaracao_p;

	if (qt_registro_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(261857,'');
		--O questionário da declaração de saúde não foi preenchido. Favor verificar.
	end if;
end if;

update	pls_declaracao_segurado
set	ie_forma_preenchimento	= 'B'
where	nr_sequencia	= nr_seq_declaracao_p;

CALL pls_alterar_status_declaracao(nr_seq_declaracao_p, ie_opcao_p, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
