SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_lib_inclusao_benef_web ( nr_sequencia_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE


nr_seq_titular_solic_w		bigint;
ds_mensagem_w			varchar(200);
ie_status_titular_w		varchar(2);

BEGIN

select	nr_seq_titular_solic
into STRICT	nr_seq_titular_solic_w
from	pls_inclusao_beneficiario
where	nr_sequencia = nr_sequencia_p;

if (coalesce(nr_seq_titular_solic_w::text, '') = '') then
	update	pls_inclusao_beneficiario
	set	ie_status	= 'P',
		dt_liberacao_solic_inclusao = clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
else
	select	ie_status
	into STRICT	ie_status_titular_w
	from	pls_inclusao_beneficiario
	where	nr_sequencia = nr_seq_titular_solic_w;
	
	if (ie_status_titular_w in ('P','A')) then
		update	pls_inclusao_beneficiario
		set	ie_status	= 'P',
			dt_liberacao_solic_inclusao = clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
	else
		ds_mensagem_w := wheb_mensagem_pck.get_texto(379090,'');
	end if;
end if;

ds_mensagem_p := ds_mensagem_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
