SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_lib_item_analise ( nr_seq_item_p bigint, nr_seq_regra_lib_p bigint, nr_seq_analise_p bigint, nr_seq_conta_p bigint, ie_tipo_item_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_liberar_imp_w		varchar(255);
nr_seq_grupo_analise_w		bigint;
nr_seq_proc_partic_w		bigint;
nr_seq_fluxo_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_grupo,
		a.nr_seq_fluxo
	from	pls_ocorrencia_benef		c,
		pls_grupo_auditor		b,
		pls_analise_glo_ocor_grupo	a
	where	a.nr_seq_grupo		= b.nr_sequencia
	and	a.nr_seq_ocor_benef	= c.nr_sequencia
	and	a.nr_seq_analise	= nr_seq_analise_p
	and	((c.nr_seq_conta_proc = nr_seq_item_p AND ie_tipo_item_p = 'P') or
		(c.nr_seq_conta_mat = nr_seq_item_p AND ie_tipo_item_p = 'M' ))
	
union all

	SELECT	a.nr_seq_grupo,
		a.nr_seq_fluxo
	from	pls_conta_glosa			c,
		pls_grupo_auditor		b,
		pls_analise_glo_ocor_grupo	a
	where	a.nr_seq_grupo		= b.nr_sequencia
	and	a.nr_seq_conta_glosa	= c.nr_sequencia
	and	a.nr_seq_analise	= nr_seq_analise_p
	and	((c.nr_seq_conta_proc = nr_seq_item_p AND ie_tipo_item_p = 'P') or
		(c.nr_seq_conta_mat = nr_seq_item_p AND ie_tipo_item_p = 'M' ))
	order by
		nr_seq_fluxo;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_proc_participante
	where	nr_seq_conta_proc	= nr_seq_item_p;


BEGIN
ie_liberar_imp_w	:= pls_obter_permissao_auditor(nr_seq_analise_p, null, nm_usuario_p, 'LC');

if (ie_tipo_item_p = 'P') then
	update	pls_conta_proc
	set	nr_seq_regra_lib 	= nr_seq_regra_lib_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia 		= nr_seq_item_p;

elsif (ie_tipo_item_p = 'M') then
	update	pls_conta_mat
	set	nr_seq_regra_lib 	= nr_seq_regra_lib_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia = nr_seq_item_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
