CREATE OR REPLACE PROCEDURE pls_lib_item_reembolso_lista ( nr_seq_reembolso_proc_lista_p bigint, nr_seq_reembolso_mat_lista_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE



lista_reembolso_w	varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_reembolso_w	smallint;
ie_contador_w		smallint;


BEGIN

if (nr_seq_reembolso_proc_lista_p IS NOT NULL AND nr_seq_reembolso_proc_lista_p::text <> '') then
	lista_reembolso_w	:= nr_seq_reembolso_proc_lista_p;
else
	lista_reembolso_w	:= nr_seq_reembolso_mat_lista_p;
end if;

ie_contador_w	:= 0;

while	(lista_reembolso_w IS NOT NULL AND lista_reembolso_w::text <> '') and
	ie_contador_w < 200 loop
	begin
	tam_lista_w		:= length(lista_reembolso_w);
	ie_pos_virgula_w	:= position(',' in lista_reembolso_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_reembolso_w	:= substr(lista_reembolso_w, 1, (ie_pos_virgula_w - 1));
		lista_reembolso_w	:= substr(lista_reembolso_w, (ie_pos_virgula_w + 1), tam_lista_w);

		if (nr_seq_reembolso_proc_lista_p IS NOT NULL AND nr_seq_reembolso_proc_lista_p::text <> '') then
			CALL pls_liberar_item_reembolso(
				nr_seq_reembolso_w,
				0,
				ie_opcao_p,
				nm_usuario_p);
		else
			CALL pls_liberar_item_reembolso(
				0,
				nr_seq_reembolso_w,
				ie_opcao_p,
				nm_usuario_p);
		end if;
	elsif (tam_lista_w <> 0) then
		if (nr_seq_reembolso_proc_lista_p IS NOT NULL AND nr_seq_reembolso_proc_lista_p::text <> '') then
			CALL pls_liberar_item_reembolso(
				lista_reembolso_w,
				0,
				ie_opcao_p,
				nm_usuario_p);
		else
			CALL pls_liberar_item_reembolso(
				0,
				lista_reembolso_w,
				ie_opcao_p,
				nm_usuario_p);
		end if;
		lista_reembolso_w	:= '';
	end if;

	ie_contador_w	:= ie_contador_w + 1;
	end;
end loop;

commit;
end;
$body$
