CREATE OR REPLACE PROCEDURE pls_limita_lote_sib ( nr_seq_lote_sib_p bigint, ie_opcao_p text, dt_re_envio_sib_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


/*
R - Opção para quando o gerar um sub - lote sib, verificar se o lote de referência está de acordo com os parâmetros
E-   Opção para quando for cadastra um lote SIB, verificar se o parâmetro 7 está S ou verificar se no mês anterior o lote SIB tem todas as importações realizadas
*/
ds_erro_w		varchar(100);
qt_registros_w		bigint;
dt_re_envio_sib_w	timestamp;
qt_dt_referencia_w	bigint;
ie_cadastrar_sub_w	varchar(1);
ie_sem_recebimento_w	varchar(1);
qt_lote_sib_w		bigint;
cd_estabelecimento_w	smallint;


BEGIN

ie_sem_recebimento_w	:= coalesce(obter_valor_param_usuario(1214, 7, Obter_Perfil_Ativo, nm_usuario_p, 0), 'N');

if (ie_opcao_p = 'R') then
	select	count(*)
	into STRICT	qt_dt_referencia_w
	from	pls_lote_sib
	where	ie_tipo_envio	= 'E'
	and	ie_status	= 'E'
	and	trunc(dt_mesano_referencia,'Month') = trunc(dt_re_envio_sib_p,'Month')
	and	nr_sequencia	= nr_seq_lote_sib_p;

	if (qt_dt_referencia_w = 0) then
		ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(280857, 'DT_RE_ENVIO_SIB_P=' || substr(to_char(dt_re_envio_sib_p,'Month'),1,9)||to_char(dt_re_envio_sib_p,'yyyy'));
	else
		if (pls_liberar_sub_lote_sib(nr_seq_lote_sib_p) = 'N') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(46650);
		end if;
	end if;
elsif (ie_opcao_p = 'E') then
	select	count(*)
	into STRICT	qt_lote_sib_w
	from	pls_lote_sib
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	nr_sequencia		<> nr_seq_lote_sib_p;

	select	count(*)
	into STRICT	qt_dt_referencia_w
	from	pls_lote_sib
	where	ie_tipo_envio		= 'E'
	and	ie_status		= 'E'
	and	((ie_resumo_importado	= 'S') or (ie_resumo_importado	= 'M'))
	and	((ie_erro_importado	= 'S') or (ie_erro_importado	= 'M'))
	and	((ie_cco_importado	= 'S') or (ie_cco_importado	= 'M'))
	and	((ie_cco_atualizado	= 'S') or (ie_cco_atualizado	= 'M'))
	and	trunc(dt_mesano_referencia,'Month') = trunc(dt_re_envio_sib_p-1,'Month')
	and	cd_estabelecimento = cd_estabelecimento_p;

	if (ie_sem_recebimento_w <> 'S') then
		if (qt_dt_referencia_w = 0) and (qt_lote_sib_w > 0) then
			ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(280862);
		end if;
	end if;
end if;

ds_erro_p :=	ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
