SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_limpar_base_conta ( nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_w		bigint;
nr_seq_lote_w		bigint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		max(cd_estabelecimento)
	from	pls_conta
	where	nr_seq_protocolo = nr_seq_protocolo_p;
			

BEGIN

open C01;
loop
fetch C01 into	
	nr_seq_conta_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	delete from	pls_conta_autor
	where		nr_seq_conta = nr_seq_conta_w;
	
	delete from 	pls_conta_historico
	where		nr_seq_conta = nr_seq_conta_w;
	
	CALL pls_deletar_coparticipacao(nr_seq_conta_w,null,'S', 'N', null, null, nm_usuario_p,cd_estabelecimento_w);
	
	delete from	pls_diagnostico_conta
	where		nr_seq_conta = nr_seq_conta_w;
	
	delete from	pls_conta_mat
	where		nr_seq_conta = nr_seq_conta_w;
	
	delete from	pls_conta_proc
	where		nr_seq_conta = nr_seq_conta_w;
	
	delete from	pls_resumo_conta
	where		nr_seq_conta = nr_seq_conta_w;

	delete from	pls_conta_glosa
	where		nr_seq_conta = nr_seq_conta_w;
	end;
end loop;
close C01;

delete from	pls_conta
where		nr_seq_protocolo = nr_seq_protocolo_p;

update	nota_fiscal
set	nr_seq_prot_res_pls   = NULL
where	nr_seq_prot_res_pls in (	SELECT	nr_sequencia
				from	pls_prot_conta_titulo
				where	nr_seq_protocolo = nr_seq_protocolo_p);
			
update	titulo_pagar
set	nr_seq_prot_conta  = NULL
where	nr_seq_prot_conta in (	SELECT	nr_sequencia
				from	pls_prot_conta_titulo
				where	nr_seq_protocolo = nr_seq_protocolo_p);

delete from	pls_prot_conta_titulo
where		nr_seq_protocolo = nr_seq_protocolo_p;

select	nr_seq_lote
into STRICT	nr_seq_lote_w
from	pls_protocolo_conta
where	nr_sequencia 	= nr_seq_protocolo_p;

delete from	pls_protocolo_conta
where		nr_sequencia	= nr_seq_protocolo_p;

delete from	pls_lote_protocolo
where		nr_sequencia	= nr_seq_lote_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
