SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_limpar_base_gestao_com ( nr_seq_cliente_p bigint, nr_seq_lead_p bigint) AS $body$
DECLARE

/*
	passar 0 no parâmetro que não deseja limpar
	nr_seq_cliente_p = PROSPECÇÃO
	nr_seq_lead_p = SOLICITAÇÃO DE LEAD
*/
BEGIN
if (nr_seq_cliente_p <> 0) then
	delete from pls_comercial_envio
	where	nr_seq_cliente = nr_seq_cliente_p;

	delete from pls_comercial_historico
	where	nr_seq_cliente = nr_seq_cliente_p;

	delete from pls_solicitacao_vendedor
	where	nr_seq_cliente = nr_seq_cliente_p;

	delete from pls_comercial_documento
	where	nr_seq_cliente = nr_seq_cliente_p;

	delete from pls_proposta_plano
	where	nr_seq_proposta in (	SELECT	nr_sequencia
					from	pls_proposta_adesao
					where	nr_seq_cliente = nr_seq_cliente_p);

	delete from pls_proposta_beneficiario
	where	nr_seq_proposta in (	SELECT	nr_sequencia
					from	pls_proposta_adesao
					where	nr_seq_cliente = nr_seq_cliente_p);

	delete from pls_proposta_adesao
	where	nr_seq_cliente = nr_seq_cliente_p;

	delete from pls_comercial_cliente
	where	nr_sequencia = nr_seq_cliente_p;
end if;


if (nr_seq_lead_p <> 0) then
	update	pls_solicitacao_vendedor
	set	nr_seq_solicitacao  = NULL
	where	nr_seq_solicitacao = nr_seq_lead_p;

	delete from pls_solicitacao_historico
	where	nr_seq_solicitacao = nr_seq_lead_p;

	delete from pls_comercial_empresa
	where	nr_seq_solicitacao = nr_seq_lead_p;

	update pls_simulacao_preco
	set	nr_seq_cliente  = NULL
	where	nr_seq_cliente in (	SELECT	nr_sequencia
					from	pls_comercial_cliente
					where	nr_seq_solicitacao = nr_seq_lead_p );

	delete from pls_comercial_cliente
	where	nr_seq_solicitacao = nr_seq_lead_p;

	delete from pls_simulacao_preco
	where	nr_seq_solicitacao = nr_seq_lead_p;

	delete from pls_solicitacao_comercial
	where	nr_sequencia = nr_seq_lead_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
