SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_limpar_base_mensalidade ( nr_seq_lote_p text) AS $body$
DECLARE


nr_seq_mensalidade_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_mensalidade
	where	nr_seq_lote = nr_seq_lote_p
	and	nr_seq_lote_p	<> 0
	
UNION ALL

	SELECT	nr_sequencia
	from	pls_mensalidade
	where	nr_seq_lote_p	= 0;


BEGIN

open C01;
loop
fetch c01 into
	nr_seq_mensalidade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_conta_coparticipacao
	set	nr_seq_mensalidade_seg  = NULL
	where	nr_seq_mensalidade_seg in (	SELECT	nr_sequencia
						from	pls_mensalidade_segurado
						where	nr_seq_mensalidade = nr_seq_mensalidade_w);

	update	pls_segurado_mensalidade a
	set	a.nr_seq_item_mensalidade  = NULL,
		ie_situacao		= 'A'
	where	a.nr_seq_item_mensalidade in (SELECT	z.nr_sequencia
						from	pls_mensalidade_segurado x,
							pls_mensalidade_seg_item z
						where	x.nr_sequencia		= z.nr_seq_mensalidade_seg
						and	x.nr_seq_mensalidade	= nr_seq_mensalidade_w);

	update	pls_segurado_mensalidade a
	set	nr_seq_mens_seg_gerado	 = NULL
	where	a.nr_seq_mens_seg_gerado in (SELECT	x.nr_sequencia
						from	pls_mensalidade_segurado x
						where	x.nr_seq_mensalidade	= nr_seq_mensalidade_w);

	update	pls_lancamento_mensalidade
	set	nr_seq_mensalidade_item  = NULL,
		ie_situacao		= 'A'
	where	nr_seq_mensalidade_item in (SELECT	z.nr_sequencia
						from	pls_mensalidade_segurado x,
							pls_mensalidade_seg_item z
						where	x.nr_sequencia		= z.nr_seq_mensalidade_seg
						and	x.nr_seq_mensalidade	= nr_seq_mensalidade_w);

	delete from pls_mensalidade_seg_adic
	where	nr_seq_mensalidade_seg in (	SELECT	nr_sequencia
						from	pls_mensalidade_segurado
						where	nr_seq_mensalidade = nr_seq_mensalidade_w);

	update	titulo_rec_liq_cc
	set	nr_seq_mens_seg_item  = NULL
	where	nr_seq_mens_seg_item in (	SELECT	nr_sequencia
						from	pls_mensalidade_seg_item
						where	nr_seq_mensalidade_seg in (	select	nr_sequencia
											from	pls_mensalidade_segurado
											where	nr_seq_mensalidade = nr_seq_mensalidade_w));

	delete	from	pls_mensalidade_sca
	where	nr_seq_item_mens in (	SELECT	z.nr_sequencia
						from	pls_mensalidade_segurado x,
							pls_mensalidade_seg_item z
						where	x.nr_sequencia		= z.nr_seq_mensalidade_seg
						and	x.nr_seq_mensalidade	= nr_seq_mensalidade_w);

	delete	from	pls_mensalidade_trib
	where	nr_seq_item_mens in (	SELECT	z.nr_sequencia
						from	pls_mensalidade_segurado x,
							pls_mensalidade_seg_item z
						where	x.nr_sequencia		= z.nr_seq_mensalidade_seg
						and	x.nr_seq_mensalidade	= nr_seq_mensalidade_w);

	delete	FROM pls_segurado_agravo_parc
	where	nr_seq_mensalidade_item	in (SELECT	z.nr_sequencia
						from	pls_mensalidade_segurado x,
							pls_mensalidade_seg_item z
						where	x.nr_sequencia		= z.nr_seq_mensalidade_seg
						and	x.nr_seq_mensalidade	= nr_seq_mensalidade_w);

	delete from pls_mensalidade_seg_item
	where	nr_seq_mensalidade_seg in (	SELECT	nr_sequencia
						from	pls_mensalidade_segurado
						where	nr_seq_mensalidade = nr_seq_mensalidade_w);

	delete from	pls_repasse_mens
	where	nr_seq_mens_seg in (	SELECT	nr_sequencia
						from	pls_mensalidade_segurado
						where	nr_seq_mensalidade = nr_seq_mensalidade_w);

	update	pls_segurado_carteira
	set	nr_seq_mensalidade_seg  = NULL
	where	nr_seq_mensalidade_seg in (	SELECT	nr_sequencia
						from	pls_mensalidade_segurado
						where	nr_seq_mensalidade = nr_seq_mensalidade_w);

	update	titulo_receber
	set	nr_seq_mens_segurado  = NULL
	where	nr_seq_mens_segurado in (	SELECT	nr_sequencia
						from	pls_mensalidade_segurado
						where	nr_seq_mensalidade = nr_seq_mensalidade_w);

	update	titulo_receber_cobr
	set	nr_seq_mensalidade	 = NULL
	where	nr_seq_mensalidade	= nr_seq_mensalidade_w;

	delete	from pls_mensalidade_historico
	where	nr_seq_mensalidade = nr_seq_mensalidade_w;

	delete	from pls_mensalidade_segurado
	where	nr_seq_mensalidade = nr_seq_mensalidade_w;

	update	titulo_receber
	set	nr_seq_mensalidade  = NULL
	where	nr_seq_mensalidade = nr_seq_mensalidade_w;

	update	nota_fiscal
	set	nr_seq_mensalidade  = NULL
	where	nr_seq_mensalidade = nr_seq_mensalidade_w;

	update	pls_pagador_amortizacao
	set	nr_seq_mensalidade	 = NULL
	where	nr_seq_mensalidade	= nr_seq_mensalidade_w;

	update	pls_mensalidade_log
	set	nr_seq_mensalidade	 = NULL
	where	nr_seq_mensalidade	= nr_seq_mensalidade_w;

	delete	from pls_mensalidade_trib
	where	nr_seq_mensalidade	= nr_seq_mensalidade_w;
	end;
end loop;
close c01;

delete	from pls_mensalidade
where	((nr_seq_lote = nr_seq_lote_p) or (nr_seq_lote_p = 0));

delete	from pls_lote_mensalidade
where	((nr_sequencia = nr_seq_lote_p) or (nr_seq_lote_p = 0));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
