CREATE OR REPLACE PROCEDURE pls_limpar_dados_conta_web ( nr_seq_conta_p bigint) AS $body$
DECLARE


ie_tipo_guia_w		varchar(2);

BEGIN

	update	pls_conta
	set	ie_carater_internacao  = NULL,
		nr_seq_tipo_atendimento  = NULL,
		nr_seq_saida_spsadt  = NULL,
		nr_seq_saida_consulta  = NULL,
		nr_seq_clinica  = NULL,
		ie_regime_internacao  = NULL,
		nr_seq_saida_int  = NULL,
		ie_obito_mulher  = NULL,
		dt_cirurgia  = NULL,
		qt_nasc_vivos  = NULL,
		qt_nasc_vivos_prematuros  = NULL,
		qt_nasc_mortos  = NULL,
		qt_obito_precoce  = NULL,
		qt_obito_tardio  = NULL,
		dt_entrada  = NULL,
		dt_alta  = NULL,
		dt_entrada_uti  = NULL,
		dt_saida_uti  = NULL,
		ie_regime_atendimento  = NULL,
		ie_saude_ocupacional  = NULL
	where	nr_sequencia = nr_seq_conta_p;
	
	
	select	max(ie_tipo_guia)
	into STRICT	ie_tipo_guia_w
	from	pls_conta
	where	nr_sequencia = nr_seq_conta_p;
	
	if (ie_tipo_guia_w <> '4') then
		update	pls_conta
		set	dt_emissao  = NULL
		where	nr_sequencia = nr_seq_conta_p;
	end if;
	
	delete  from pls_diagnostico_nasc_vivo
	where	nr_seq_conta = nr_seq_conta_p;
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
