CREATE OR REPLACE PROCEDURE pls_limpar_mala_dir_html5 ( nm_usuario_p text, ie_tipo_dest bigint) AS $body$
BEGIN

if (ie_tipo_dest = 0) then
	delete from pls_mala_direta where nm_usuario = nm_usuario_p and (ds_tipo_destinatario = 'Estipulante PJ' or ds_tipo_destinatario = 'Estipulante PF' or ds_tipo_destinatario = 'SubEstipulante' or ds_tipo_destinatario = 'Contato');
elsif (ie_tipo_dest = 1) then
	delete from pls_mala_direta where nm_usuario = nm_usuario_p and (ds_tipo_destinatario = 'Titular' or ds_tipo_destinatario = 'Dependentes');
elsif (ie_tipo_dest = 2) then
	delete from pls_mala_direta where nm_usuario = nm_usuario_p and ds_tipo_destinatario = 'Pagador';
elsif (ie_tipo_dest = 3) then
	delete from pls_mala_direta where nm_usuario = nm_usuario_p and ds_tipo_destinatario = 'Cooperado';
elsif (ie_tipo_dest = 4) then
	delete from pls_mala_direta where nm_usuario = nm_usuario_p and coalesce(ds_tipo_destinatario::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
