CREATE OR REPLACE PROCEDURE pls_limpar_tabela_carga ( ie_tipo_importacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* ie_tipo_importacao_p
	1 - importar_produto
	2 - importar_preco_comercializacao
	3 - importar_contrato
	4 - importar_pagador
	5 - importar_beneficiario
	6 - importar_comercial
*/
BEGIN

if (ie_tipo_importacao_p	= 1) then
	CALL exec_sql_dinamico(nm_usuario_p, 'truncate table w_pls_carga_produto');
elsif (ie_tipo_importacao_p	= 2) then
	CALL exec_sql_dinamico(nm_usuario_p, 'truncate table w_pls_carga_preco');
elsif (ie_tipo_importacao_p	= 3) then
	CALL exec_sql_dinamico(nm_usuario_p, 'truncate table w_pls_carga_contrato');
elsif (ie_tipo_importacao_p	= 4) then
	CALL exec_sql_dinamico(nm_usuario_p, 'truncate table w_pls_carga_pagador');
elsif (ie_tipo_importacao_p	= 5) then
	CALL exec_sql_dinamico(nm_usuario_p, 'truncate table w_pls_carga_segurado');
elsif (ie_tipo_importacao_p	= 6) then
	CALL exec_sql_dinamico(nm_usuario_p, 'truncate table w_pls_carga_comercial_hist');
	CALL exec_sql_dinamico(nm_usuario_p, 'truncate table w_pls_carga_comercial');
end if;

