CREATE OR REPLACE PROCEDURE pls_limpar_xml_pend_imp_web ( ie_tipo_processo_p bigint) AS $body$
DECLARE

 
nr_seq_protocolo_w	bigint;
nr_seq_xml_arquivo_w	bigint;
cd_estabelecimento_w	bigint := 1;

C01 CURSOR FOR 
	SELECT 	nr_sequencia 
	from	pls_xml_arquivo 
	where	ie_status = 'IMP' 
	and	ie_tipo_processo = ie_tipo_processo_p;
	

BEGIN 
 
cd_estabelecimento_w := (pls_parametro_operadora_web('EST'))::numeric;
 
open C01;
loop 
fetch C01 into 
	nr_seq_xml_arquivo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_protocolo_w 
		from	pls_protocolo_conta 
		where	nr_seq_xml_arquivo = nr_seq_xml_arquivo_w;
		 
		if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then 
			begin 
				CALL pls_excluir_protocolo_imp(nr_seq_protocolo_w,'TASY', cd_estabelecimento_w);
			exception 
			when others then 
				nr_seq_protocolo_w := null;
			end;
		end if;
	end;
end loop;
close C01;
 
update 	pls_xml_arquivo 
set	ie_status = 'VAL', 
	dt_inicio_importacao  = NULL 
where	ie_status = 'IMP' 
and	ie_tipo_processo = ie_tipo_processo_p;
 
update 	pls_xml_lote 
set	ie_status = 'VAL' 
where	ie_status = 'IMP' 
and	ie_tipo_processo = ie_tipo_processo_p;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
