SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liquidar_titulos_rescisao ( nr_seq_pagador_p bigint, dt_rescisao_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_w			bigint;
vl_saldo_titulo_w		double precision;
ie_liquidar_titulos_rescisao_w	varchar(1);
cd_tipo_receb_rescisao_w	integer;
nr_seq_trans_fin_rescisao_w	bigint;
ie_gerar_cobr_rescisao_w	varchar(1);
qt_titulo_cobranca_w		bigint;
nr_seq_cobranca_w		bigint;
dt_emissao_w			titulo_receber.dt_emissao%type;
dt_baixa_w			timestamp;
nr_seq_baixa_w			titulo_receber_liq.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	a.nr_titulo,
		a.vl_saldo_titulo,
		a.dt_emissao
	from	pls_mensalidade b,
		titulo_receber a
	where	a.nr_seq_mensalidade	= b.nr_sequencia
	and	b.nr_seq_pagador	= nr_seq_pagador_p
	and	coalesce(a.dt_liquidacao::text, '') = ''
	and	((ie_liquidar_titulos_rescisao_w = 'F' AND a.dt_pagamento_previsto > dt_rescisao_p) or (ie_liquidar_titulos_rescisao_w <> 'F'));


BEGIN

if (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (dt_rescisao_p IS NOT NULL AND dt_rescisao_p::text <> '') then
	select	coalesce(max(ie_liquidar_titulos_rescisao),'N'),
		max(cd_tipo_receb_rescisao),
		max(nr_seq_trans_fin_rescisao),
		coalesce(max(ie_gerar_cobr_rescisao),'N')
	into STRICT	ie_liquidar_titulos_rescisao_w,
		cd_tipo_receb_rescisao_w,
		nr_seq_trans_fin_rescisao_w,
		ie_gerar_cobr_rescisao_w
	from	pls_parametros_cr a
	where	a.cd_estabelecimento	= cd_estabelecimento_p;
	
	if (ie_liquidar_titulos_rescisao_w in ('S','F')) then
		if (coalesce(cd_tipo_receb_rescisao_w::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(185661);
			--'Não foi informado o tipo de recebimento para liquidação dos títulos na rescisão.' || chr(13) || chr(10) ||

			--				'Verifique em OPS - Gestão de Operadoras -> Parâmetros OPS -> Contas a Receber.#@#@');
		end if;
		
		if (coalesce(nr_seq_trans_fin_rescisao_w::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(185662);
			--'Não foi informada a transação financeira para liquidação dos títulos na rescisão.' || chr(13) || chr(10) ||

			--				'Verifique em OPS - Gestão de Operadoras -> Parâmetros OPS -> Contas a Receber.#@#@');
		end if;
		
		open c01;
		loop
		fetch c01 into
			nr_titulo_w,
			vl_saldo_titulo_w,
			dt_emissao_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			if (ie_gerar_cobr_rescisao_w = 'S') then
				select	count(*)
				into STRICT	qt_titulo_cobranca_w
				from	cobranca
				where	nr_titulo	= nr_titulo_w;
				
				if (qt_titulo_cobranca_w = 0) then
					CALL importar_cobranca(nr_titulo_w, 'N', nm_usuario_p, null);
					
					select	max(nr_sequencia)
					into STRICT	nr_seq_cobranca_w
					from	cobranca a
					where	nr_titulo	= nr_titulo_w;
					
					CALL tratar_destino_cobranca(nr_seq_cobranca_w,null,cd_estabelecimento_p,nm_usuario_p);
				end if;
			end if;
			
			if (dt_emissao_w > dt_rescisao_p) then
				dt_baixa_w	:= dt_emissao_w;
			else
				dt_baixa_w	:= dt_rescisao_p;
			end if;
			
			CALL baixa_titulo_receber(cd_estabelecimento_p,
				cd_tipo_receb_rescisao_w,
				nr_titulo_w,
				nr_seq_trans_fin_rescisao_w,
				vl_saldo_titulo_w,
				dt_baixa_w,
				nm_usuario_p,
				null,
				null,
				null,
				0,
				0);
			
			select	max(nr_sequencia)
			into STRICT	nr_seq_baixa_w
			from	titulo_receber_liq
			where	nr_titulo	= nr_titulo_w;
			
			update	titulo_receber_liq			
			set	ie_baixa_rescisao_contrato	= 'S'
			where	nr_titulo	= nr_titulo_w
			and	nr_sequencia	= nr_seq_baixa_w;
			
			CALL atualizar_saldo_tit_rec(nr_titulo_w,nm_usuario_p);
			end;
		end loop;
		close c01;
	end if;
end if;

/* Não pode ter commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_liquidar_titulos_rescisao ( nr_seq_pagador_p bigint, dt_rescisao_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

