SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_localizar_material (ds_localizar_p text, nr_seq_localizar_p bigint, ds_material_p INOUT text) AS $body$
DECLARE


nr_sequencia_w 		bigint;
nr_seq_mat_w 		bigint;
nr_seq_superior_w	bigint;
nr_seq_superior_w2	bigint;
nr_seq_localizar_w	smallint;

c001 CURSOR FOR
	SELECT 	a.nr_sequencia,
	 	a.nr_seq_superior
	from 	pls_estrutura_material a
	where 	upper(a.ds_estrutura) like '%'|| upper(ds_localizar_p) || '%'
	order by   a.nr_seq_superior,
		   a.ds_estrutura;


BEGIN

nr_seq_localizar_w := nr_seq_localizar_p;
nr_sequencia_w	 := 0;
open c001;
loop
	fetch c001 into
		nr_seq_mat_w,
		nr_seq_superior_w;
	EXIT WHEN NOT FOUND; /* apply on c001 */

	nr_sequencia_w := nr_sequencia_w + 1;

	if (nr_sequencia_w > nr_seq_localizar_p) then

		begin
		ds_material_p := to_char(nr_seq_mat_w);
		nr_seq_localizar_w := nr_seq_localizar_p + 1;

		exit;
		end;
	end if;
end loop;
close c001;

if (ds_material_p IS NOT NULL AND ds_material_p::text <> '') and (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
	begin
	ds_material_p := to_char(nr_seq_superior_w) || ',' || ds_material_p;
	nr_seq_superior_w2 := nr_seq_superior_w;
	while (nr_seq_superior_w2 IS NOT NULL AND nr_seq_superior_w2::text <> '') loop
		begin
		select nr_seq_superior
		into STRICT	 nr_seq_superior_w2
		from pls_estrutura_material
		where nr_sequencia = nr_seq_superior_w;

		if (nr_seq_superior_w2 IS NOT NULL AND nr_seq_superior_w2::text <> '') then
			ds_material_p := to_char(nr_seq_superior_w2) || ',' || ds_material_p;
		end if;

		nr_seq_superior_w := nr_seq_superior_w2;
		end;
	end loop;
	end;
end if;

nr_seq_localizar_w := 0;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
