CREATE OR REPLACE PROCEDURE pls_lote_alterar_status ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
update 	pls_lote_carteira 
set	ie_situacao 	= 'D', 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_sequencia 	= nr_sequencia_p;
 
CALL pls_alt_valid_cart_lote_venc(nr_sequencia_p,cd_estabelecimento_p,'N',nm_usuario_p);
			 
end;
