CREATE OR REPLACE PROCEDURE pls_lote_comunicacao_inter_web ( nr_seq_usu_prest_web_p bigint, nr_seq_prestador_p bigint, cd_prestador_p text, cd_cgc_p text, cd_cpf_p text, cd_versao_p text, ds_arquivo_p text, nm_usuario_p text, ie_origem_lote_p text, nr_seq_lote_p INOUT bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Criar o lote de comuinicação de internação
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  X] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_lote_w			bigint;
nr_seq_prestador_w		bigint;


BEGIN

select	nextval('pls_lote_comunicacao_inter_seq')
into STRICT	nr_seq_lote_w
;

nr_seq_prestador_w := nr_seq_prestador_p;

if ( (cd_prestador_p IS NOT NULL AND cd_prestador_p::text <> '') and coalesce(nr_seq_prestador_p::text, '') = '' ) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_prestador_w
	from	pls_prestador
	where	cd_prestador = cd_prestador_p
	and	ie_situacao = 'A';
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_prestador_w
	from	pls_prestador
	where	cd_cgc = cd_cgc_p
	and	ie_situacao = 'A';
elsif (cd_cpf_p IS NOT NULL AND cd_cpf_p::text <> '') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_prestador_w
	from	pls_prestador a,
		pessoa_fisica b
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	b.nr_cpf = cd_cpf_p
	and	a.ie_situacao = 'A';
end if;

insert into pls_lote_comunicacao_inter( dt_recebimento, dt_atualizacao, nm_usuario,
					dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_prestador,
					cd_prestador, nr_seq_usu_prest_web, ds_arquivo,
					nr_sequencia, ie_origem_lote, ie_status,
					nr_cpf_prestador, cd_cgc_prestador, cd_versao_tiss)
				values ( clock_timestamp(), clock_timestamp(), nm_usuario_p,
					clock_timestamp(), nm_usuario_p, nr_seq_prestador_w,
					cd_prestador_p, nr_seq_usu_prest_web_p, ds_arquivo_p,
					nr_seq_lote_w, ie_origem_lote_p, 'R',
					cd_cpf_p, cd_cgc_p, cd_versao_p);


nr_seq_lote_p := nr_seq_lote_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
