CREATE OR REPLACE PROCEDURE pls_lote_confirmar_envio ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_carteira_w		bigint;
cd_estabelecimento_w		smallint;

C01 CURSOR FOR
	SELECT	a.nr_seq_seg_carteira,
		b.cd_estabelecimento
	from	pls_carteira_emissao	a,
		pls_lote_carteira	b
	where	a.nr_seq_lote	= nr_sequencia_p
	and	b.nr_sequencia	= a.nr_seq_lote;

BEGIN

update	pls_lote_carteira
set	dt_envio	= clock_timestamp(),
	ie_situacao	= 'D',
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_seq_carteira_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_alterar_estagios_cartao(nr_seq_carteira_w,clock_timestamp(),'3',cd_estabelecimento_w,nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
