CREATE OR REPLACE PROCEDURE pls_marcar_conferido_auditoria ( nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_requisicao_w		bigint;
ds_mensagem_w			varchar(255);


BEGIN

update	pls_auditoria
set	nm_usuario			= nm_usuario_p,
	dt_atualizacao			= clock_timestamp(),
	dt_conferencia_intercambio	= clock_timestamp() + interval '1 days'
where	nr_sequencia			= nr_seq_auditoria_p;

select	nr_seq_requisicao
into STRICT	nr_seq_requisicao_w
from	pls_auditoria
where	nr_sequencia			= nr_seq_auditoria_p;

ds_mensagem_w := substr(wheb_mensagem_pck.get_texto(306068, 'dt_referencia=' || to_char(clock_timestamp(),'dd/mm/yyyy hh:mm:ss')),1,255);
insert into pls_requisicao_historico(nr_sequencia, nr_seq_requisicao,
	dt_historico, dt_atualizacao, nm_usuario,
	ds_historico, ie_origem_historico, ie_tipo_historico)
values (	nextval('pls_requisicao_historico_seq'), nr_seq_requisicao_w,
	clock_timestamp(), clock_timestamp(), nm_usuario_p,
	ds_mensagem_w||obter_nome_usuario(nm_usuario_p),'M','M');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
