CREATE OR REPLACE PROCEDURE pls_marcar_envio_item_giss ( nr_seq_item_giss_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Marcar/desmarcar os títulos/NFs que serão enviados no arquivo GISS
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/BEGIN
if (nr_seq_item_giss_p IS NOT NULL AND nr_seq_item_giss_p::text <> '') then
	update	pls_tomador_prestador_giss
	set	ie_envio 	= CASE WHEN coalesce(ie_envio,'S')='S' THEN 'N'  ELSE 'S' END
	where	nr_sequencia	= nr_seq_item_giss_p;

	commit;
end if;

end;
$body$
