SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_mat_alterar_estruturas ( nm_usuario_p text, nr_seq_estrutura_p bigint, nr_sequencias_p text ) AS $body$
DECLARE


nr_sequencias_w			varchar(1000);	/* Conjunto de identificadores  */
ie_pos_virgula_w			smallint;	/* posicao da virgula */
tam_lista_w			bigint;	/* tamanha da lista de identificadores */
nr_sequencia_w			bigint;	/* identificador atual */
nr_controle_loop_w			smallint := 0;	/* controle do loop */
BEGIN
if (nr_sequencias_p IS NOT NULL AND nr_sequencias_p::text <> '') then
	begin
	nr_sequencias_w := nr_sequencias_p;

	while(nr_sequencias_w IS NOT NULL AND nr_sequencias_w::text <> '') and (nr_controle_loop_w < 150) loop
		begin
		tam_lista_w	:= length(nr_sequencias_w);
		ie_pos_virgula_w	:= position(',' in nr_sequencias_w);
		nr_sequencia_w	:= 0;

		if (ie_pos_virgula_w <> 0) then
			begin
			nr_sequencia_w	:= (substr(nr_sequencias_w,1,(ie_pos_virgula_w - 1)))::numeric;
			nr_sequencias_w	:= substr(nr_sequencias_w,(ie_pos_virgula_w + 1),tam_lista_w);
			end;
		else
			begin
			nr_sequencia_w	:= (nr_sequencias_w)::numeric;
			nr_sequencias_w	:= null;
			end;
		end if;

		if (coalesce(nr_sequencia_w,0) > 0) then
			begin
			CALL pls_material_alterar_estrutura(nm_usuario_p, nr_seq_estrutura_p, nr_sequencia_w);
			end;
		end if;

		nr_controle_loop_w := nr_controle_loop_w + 1;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
