CREATE OR REPLACE PROCEDURE pls_mens_gravar_log_geracao ( nr_seq_lote_p pls_lote_mensalidade.nr_sequencia%type, ie_etapa_p text, ie_inicio_fim_p text, nm_usuario_p usuario.nm_usuario%type, nr_seq_log_p INOUT pls_mens_log_geracao.nr_sequencia%type) AS $body$
DECLARE


--ie_etapa domínio 7854 - OPS - Etapas para geração da mensalidade
nr_seq_log_w	pls_mens_log_geracao.nr_sequencia%type;
ds_conteudo_w			varchar(4000);
ie_leitura_w			boolean := true;


BEGIN

if (pls_mensalidade_util_pck.get_parametro_mens = 'S') then
	if (ie_inicio_fim_p = 'I') then
		ds_conteudo_w := 'Etapa iniciada'||' - '|| substr(obter_valor_dominio(7854,ie_etapa_p),1,255);
		CALL pls_utl_file_pck.escrever(ds_conteudo_w);

	elsif (ie_inicio_fim_p = 'F') then
		ds_conteudo_w := 'Etapa finalizada'||' - '||substr(obter_valor_dominio(7854,ie_etapa_p),1,255);
		CALL pls_utl_file_pck.escrever(ds_conteudo_w);
		ds_conteudo_w := '';
		CALL pls_utl_file_pck.escrever(ds_conteudo_w);
	end if;
end if;

if (ie_inicio_fim_p = 'I') then
	select	nextval('pls_mens_log_geracao_seq')
	into STRICT	nr_seq_log_w
	;

	insert	into	pls_mens_log_geracao(	nr_sequencia, nr_seq_lote, ie_etapa,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			dt_inicio_geracao)
	values (	nr_seq_log_w, nr_seq_lote_p, ie_etapa_p,
			clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			clock_timestamp());

	nr_seq_log_p	:= nr_seq_log_w;
elsif (ie_inicio_fim_p = 'F') then
	update	pls_mens_log_geracao
	set	dt_fim_geracao	= clock_timestamp(),
		ds_tempo_geracao = pls_obter_dif_data_horario(dt_inicio_geracao,clock_timestamp())
	where	nr_sequencia	= nr_seq_log_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
