SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_mens_grupo_cont_beforepost ( nr_contrato_p bigint, nr_seq_contrato_inter_p bigint) AS $body$
DECLARE



nr_sequencia_w	bigint;
qt_existe_w	bigint;


BEGIN

if (coalesce(nr_contrato_p,0) > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	pls_contrato
	where	nr_contrato = nr_contrato_p;

	if (coalesce(nr_sequencia_w::text, '') = '') then
		-- O contrato informado não existe. Verifique !
		CALL wheb_mensagem_pck.exibir_mensagem_abort(149572);
	end if;

end if;

if (coalesce(nr_seq_contrato_inter_p,0) > 0) then

	select	count(*)
	into STRICT	qt_existe_w
	from	pls_intercambio
	where	nr_sequencia = nr_seq_contrato_inter_p;

	if (qt_existe_w = 0) then
		-- O contrato de intercâmbio informado não existe. Verifique !
		CALL wheb_mensagem_pck.exibir_mensagem_abort(149574);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
