CREATE OR REPLACE PROCEDURE pls_mens_item_manutenc_interc ( nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type, dt_referencia_p pls_mensalidade_segurado.dt_mesano_referencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


vl_manutencao_w		pls_regra_vl_intercambio.vl_manutencao%type;
ie_inseriu_item_w	varchar(1);


BEGIN

if (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	select	sum(vl_manutencao)
	into STRICT	vl_manutencao_w
	from	pls_regra_vl_intercambio
	where	nr_seq_intercambio	= nr_seq_intercambio_p
	and	dt_referencia_p between coalesce(dt_inicio_vigencia,dt_referencia_p) and coalesce(dt_fim_vigencia,dt_referencia_p);

	if (coalesce(vl_manutencao_w,0) > 0) then
		ie_inseriu_item_w := pls_mens_itens_pck.add_item_manutenc_interc(nr_seq_mensalidade_seg_p, '34', vl_manutencao_w, 'N', '', ie_inseriu_item_w);
	end if;
end if;

end;
$body$
