CREATE OR REPLACE PROCEDURE pls_mens_item_via_carteira ( nr_seq_mensalidade_seg_p pls_mensalidade_segurado.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ie_inseriu_item_w	varchar(1);

C01 CURSOR(	nr_seq_segurado_pc		pls_segurado.nr_sequencia%type,
		ie_mens_cobrar_cart_ant_pc	text) FOR
	SELECT	nr_sequencia		nr_seq_seg_carteira,
		null			nr_seq_seg_carteira_ant,
		vl_via_adicional
	from	pls_segurado_carteira
	where	nr_seq_segurado		= nr_seq_segurado_pc
	and	coalesce(nr_seq_mensalidade_seg::text, '') = ''
	
union

	SELECT	null			nr_seq_seg_carteira,
		nr_sequencia		nr_seq_seg_carteira_ant,
		vl_via_adicional
	from	pls_segurado_cart_ant
	where	nr_seq_segurado 	= nr_seq_segurado_pc
	and	ie_mens_cobrar_cart_ant_pc = 'S'
	and	coalesce(nr_seq_mensalidade_seg::text, '') = '';

BEGIN

for r_c01_w in C01(	nr_seq_segurado_p,
			pls_mensalidade_util_pck.get_ie_mens_cobrar_cart_ant) loop
	begin
	if (coalesce(r_c01_w.vl_via_adicional,0) > 0) then
		ie_inseriu_item_w := pls_mens_itens_pck.add_item_via_cart(	nr_seq_mensalidade_seg_p, '11', r_c01_w.vl_via_adicional, r_c01_w.nr_seq_seg_carteira, r_c01_w.nr_seq_seg_carteira_ant, '', ie_inseriu_item_w);

		if (ie_inseriu_item_w = 'S') then
			if (r_c01_w.nr_seq_seg_carteira IS NOT NULL AND r_c01_w.nr_seq_seg_carteira::text <> '') then
				update	pls_segurado_carteira
				set	nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_p
				where	nr_sequencia		= r_c01_w.nr_seq_seg_carteira;
			elsif (r_c01_w.nr_seq_seg_carteira_ant IS NOT NULL AND r_c01_w.nr_seq_seg_carteira_ant::text <> '') then
				update	pls_segurado_cart_ant
				set	nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_p
				where	nr_sequencia		= r_c01_w.nr_seq_seg_carteira_ant;
			end if;
		end if;
	end if;
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
