SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_modificar_dados_guia_auto ( nr_seq_analise_p bigint, nr_seq_guia_p bigint, cd_medico_solicitante_p text, nr_seq_prestador_p bigint, ie_tipo_guia_p text, ie_regime_internacao_p text, qt_dia_solicitado_p bigint, dt_admissao_hosp_p text, ie_carater_internacao_p text, nr_seq_clinica_p bigint, nm_usuario_p text, cd_doenca_cid_p text, ie_indicacao_acid_p bigint, cd_unimed_exec_p text, nr_seq_tipo_acomodacao_p text) AS $body$
DECLARE


nr_seq_uni_exec_w	varchar(10);
dt_admissao_hosp_w	timestamp;
qt_diagnostico_guia_w   bigint;


BEGIN
/* Francisco - 14/08/2013 - OS 632384, adicionado campo com máscara, sem fazer conversão pelo Delphi */

if (dt_admissao_hosp_p = '  /  /    ') then
	dt_admissao_hosp_w	:= null;
else
	dt_admissao_hosp_w	:= to_date(dt_admissao_hosp_p,'dd/mm/yyyy');
end if;

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	if (cd_unimed_exec_p IS NOT NULL AND cd_unimed_exec_p::text <> '') then
		nr_seq_uni_exec_w	:= pls_obter_seq_cooperativa(cd_unimed_exec_p);
	end if;

	update	pls_guia_plano
	set		
		cd_medico_solicitante	= cd_medico_solicitante_p,
		nr_seq_prestador		= nr_seq_prestador_p,
		ie_tipo_guia		= ie_tipo_guia_p,
		ie_regime_internacao	= ie_regime_internacao_p,
		qt_dia_solicitado	      	= qt_dia_solicitado_p,
		dt_admissao_hosp	      	= dt_admissao_hosp_w,
		ie_carater_internacao	= ie_carater_internacao_p,
		nr_seq_clinica		= nr_seq_clinica_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nr_seq_tipo_acomodacao  	= nr_seq_tipo_acomodacao_p,
		nr_seq_uni_exec		= (nr_seq_uni_exec_w)::numeric
	where	nr_sequencia		= nr_seq_guia_p;

	select  count(1)
	into STRICT    qt_diagnostico_guia_w
	from    pls_diagnostico
	where   nr_seq_guia      = nr_seq_guia_p
	and     ie_classificacao = 'P';
		
	if (qt_diagnostico_guia_w > 0) then
		if (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '') and (ie_indicacao_acid_p IS NOT NULL AND ie_indicacao_acid_p::text <> '') then
			update  pls_diagnostico
			set     cd_doenca                = cd_doenca_cid_p,
					ie_indicacao_acidente    = ie_indicacao_acid_p
			where   nr_seq_guia              = nr_seq_guia_p
			and     ie_classificacao         = 'P';
		elsif (ie_indicacao_acid_p IS NOT NULL AND ie_indicacao_acid_p::text <> '') then
			update  pls_diagnostico
			set     ie_indicacao_acidente    = ie_indicacao_acid_p,
					cd_doenca                = cd_doenca_cid_p
			where   nr_seq_guia              = nr_seq_guia_p
			and     ie_classificacao = 'P';
		else
			if (pls_obter_versao_tiss  <> '2.02.03') then
					CALL wheb_mensagem_pck.exibir_mensagem_abort(357375);--O campo Indicação de acidente é obrigatório! 
			else
					update  pls_diagnostico
					set     ie_indicacao_acidente    = ie_indicacao_acid_p,
							cd_doenca                = cd_doenca_cid_p
					where   nr_seq_guia              = nr_seq_guia_p
					and     ie_classificacao         = 'P';
			end if;
		end if;
	else
		if (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '') or (ie_indicacao_acid_p IS NOT NULL AND ie_indicacao_acid_p::text <> '') then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(356823);--O campo não pode ser alterado, não existe informação de diagnóstico na guia!.
		end if;
	end if;
end if;

if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') then
	update	pls_auditoria
	set		cd_medico_solicitante	= cd_medico_solicitante_p,
			nr_seq_prestador	= nr_seq_prestador_p,
			ie_tipo_guia		= ie_tipo_guia_p
	where	nr_sequencia		= nr_seq_analise_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_modificar_dados_guia_auto ( nr_seq_analise_p bigint, nr_seq_guia_p bigint, cd_medico_solicitante_p text, nr_seq_prestador_p bigint, ie_tipo_guia_p text, ie_regime_internacao_p text, qt_dia_solicitado_p bigint, dt_admissao_hosp_p text, ie_carater_internacao_p text, nr_seq_clinica_p bigint, nm_usuario_p text, cd_doenca_cid_p text, ie_indicacao_acid_p bigint, cd_unimed_exec_p text, nr_seq_tipo_acomodacao_p text) FROM PUBLIC;

