SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_modificar_guia_ref_pos ( cd_guia_p pls_analise_conta.cd_guia%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

qt_benef_iguais_w	integer;


BEGIN

--Verifica se o beneficiário e a congênere são os mesmos entre a análise e a guia que está tentando vincular
select 	count(1)
into STRICT	qt_benef_iguais_w
from   	pls_conta_v a
where  	nr_seq_analise = nr_seq_analise_p
and    	exists (SELECT  1
		from   pls_conta_v b
		where  b.cd_guia = cd_guia_p
		and    b.nr_seq_segurado = a.nr_seq_segurado
		and    b.nr_seq_congenere_prot = a.nr_seq_congenere_prot);

--Beneficiários e/ou congêneres diferentes entre análise e guia da análise pós que se tenta referenciar
if (qt_benef_iguais_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(354704);
end if;

update	pls_conta
set 	cd_guia_pos_estab = cd_guia_p
where	nr_seq_analise 	= nr_seq_analise_p;

--Se não apagar a referência de análise anterior, não chegará a gerar uma nova analise pós estab.
update	pls_analise_conta
set	nr_seq_analise_ref  = NULL
where	nr_seq_analise_ref = nr_seq_analise_p
and	ie_origem_analise  = 2;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
