CREATE OR REPLACE PROCEDURE pls_obter_arred_fat_xml ( nr_seq_pagador_p pls_contrato_pagador.nr_sequencia%type, dt_referencia_p timestamp, qt_servico_p pls_fat_regra_arred.qt_arredondamento%type, qt_retorno_p INOUT pls_fat_regra_arred.qt_arredondamento%type) AS $body$
DECLARE

					 
dt_referencia_w		timestamp;

C01 CURSOR(	nr_seq_pagador_pc	pls_contrato_pagador.nr_sequencia%type, 
		dt_referencia_pc	timestamp, 
		qt_servico_pc		pls_fat_regra_arred.qt_arredondamento%type) FOR 
	SELECT	qt_arredondamento 
	from	pls_fat_regra_arred 
	where	qt_servico_p between qt_minimo and qt_maximo 
	and	dt_referencia_w between trunc(dt_inicio_vigencia) and coalesce(dt_fim_vigencia,dt_referencia_w) 
	and	((coalesce(nr_seq_pagador::text, '') = '') or (nr_seq_pagador = nr_seq_pagador_pc)) 
	order by nr_seq_pagador;

BEGIN 
dt_referencia_w := trunc(dt_referencia_p);
 
for r_C01_w in C01( nr_seq_pagador_p, dt_referencia_w, qt_servico_p) loop 
	qt_retorno_p	:= r_C01_w.qt_arredondamento;
end loop;
 
qt_retorno_p := coalesce(qt_retorno_p,qt_servico_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
