CREATE OR REPLACE PROCEDURE pls_obter_atributos_regra_web (cd_funcionalidade_p text, nr_seq_atributo_web_p bigint, ie_tipo_guia_p text, ie_valida_abreviatura_sib_p INOUT text, ie_obrigatorio_p INOUT text, ie_somente_maiusculo_p INOUT text, ds_info_label_atributo_p INOUT text, ie_visualizar_campo_p INOUT text) AS $body$
DECLARE


ie_valida_abreviatura_sib_w	varchar(1)  := 'N';
ie_obrigatorio_w		varchar(1)  := 'N';
ie_somente_maiusculo_w		varchar(1) := 'N';
