ie_entrou_regra_restricao_w	boolean := false;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_restricao_funcao_web
	where	ie_funcionalidade = cd_funcionalidade_p
	and	coalesce(ie_tipo_guia,ie_tipo_guia_p) = ie_tipo_guia_p
