C02 CURSOR FOR
	SELECT	ie_obrigatorio
	from	pls_restricao_atrib_web
	where	nr_seq_rest_funcao_web = nr_seq_restr_funcao_w
	and	nr_seq_atributo_web = nr_seq_atributo_web_p;

