		ie_obrigatorio,
		ie_somente_maiusculo,
		substr(ds_info_label_atributo,1,500),
		ie_visualizar_campo
	from	pls_regra_atributo_web
	where	nr_seq_atributo_web = nr_seq_atributo_web_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_restr_funcao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
