ie_obrigatorio_p := ie_obrigatorio_w;
ie_somente_maiusculo_p	:= ie_somente_maiusculo_w;
ds_info_label_atributo_p := ds_info_label_atributo_w;
ie_visualizar_campo_p := ie_visualizar_campo_w;

